import React, { useState, useRef, useEffect } from 'react'
import { useLoader, useFrame } from 'react-three-fiber'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader'
import * as THREE from 'three'

export const Map = () => {

  const { nodes, materials } = useLoader(GLTFLoader, '/map.gltf')

  const mapRef = useRef();
  const mapDummyPosition = new THREE.Vector3(0, -0.8, 0);
  const mapDummyRotation = new THREE.Quaternion(0, -Math.PI, 0, 0);
  const mapDummyScale = new THREE.Vector3(1, 1, 1);

  useFrame(() => {
    // Target cametra during the arrival: follows Kjell + slow move towards map
    mapRef.current.position.lerp(mapDummyPosition, 0.0175);
    mapRef.current.scale.lerp(mapDummyScale, 0.015);
    if (mapRef.current.position.x >= -2500) {
      mapRef.current.quaternion.slerp(mapDummyRotation, 0.0002);
    }
  });

  return (
    <group ref={mapRef} position={[-5000, -500, -5000]} rotation={[0, 0, 0]} scale={[0.5, 0.5, 0.5]} dispose={null} >
      <mesh
        geometry={nodes.Shockwave.geometry}
        material={materials.basicWhite}
        position={[4.15, 0.06, 0.85]}
        scale={[0.23, 0.23, 0.23]}
      />
      <mesh geometry={nodes.Mesh001.geometry} material={nodes.Mesh001.material} />
      <mesh geometry={nodes.Mesh001_1.geometry} material={alley} />
      <mesh geometry={nodes.Mesh001_2.geometry} material={sand_0} />
      <mesh geometry={nodes.Mesh001_3.geometry} material={grass_0} />
      <mesh geometry={nodes.Mesh001_4.geometry} material={sand_1} />
      <mesh geometry={nodes.Mesh001_5.geometry} material={grass_2} />
      <mesh geometry={nodes.Mesh001_6.geometry} material={grass_1} />
      <group position={[-12.73, 0.67, 0.61]} rotation={[0, Math.PI / 2, 0]} scale={[3.09, 1.18, 2.8]}>
        <mesh geometry={nodes.Cube003_1.geometry} material={nodes.Cube003_1.material} />
        <mesh geometry={nodes.Cube003_2.geometry} material={wall_1} />
        <mesh geometry={nodes.Cube003_3.geometry} material={nodes.Cube003_3.material} />
      </group>
      <group position={[-10.53, 1.3, 12.72]} rotation={[-Math.PI, 0, -Math.PI]} scale={[3.56, 1.37, 2.01]}>
        <mesh geometry={nodes.Cube004_1.geometry} material={nodes.Cube004_1.material} />
        <mesh geometry={nodes.Cube004_2.geometry} material={wall_3} />
